import React from "react";

import { Props } from "./types";
import * as Styled from "./style";

import ArrowIcon from '../../assets/iconArrow.svg';

const Banner = ({}: Props) => {
  return (
    <Styled.MainBanner display={['flex', 'flex', 'flex']}
    justifyContent={['center', 'center', 'space-between']}>

      <Styled.Title fontSize={[30, 20, 10]} >
        Funk explode em
        <Styled.SubTitle>
          conteúdos exclusivos?
        </Styled.SubTitle>
      </Styled.Title>

      <Styled.Description fontSize={[30, 20, 10]} >
        Já pensou?! Ter acesso a músicas e videoclipes 
        exclusivos do: MC Luan da BS, MC L Da Vinte,
        MC Kaio e cia.?
      </Styled.Description>

      <Styled.BtnTitle fontSize={[30, 20, 10]} >
        Então, se <Styled.BtnTitleGlow>inscreve</Styled.BtnTitleGlow> aí
      </Styled.BtnTitle>

      <Styled.LinkBtn fontSize={[30, 20, 10]} >
        Saiba mais <ArrowIcon />
      </Styled.LinkBtn>

    </Styled.MainBanner>
  )
};

export default Banner;
