import styled from 'styled-components';
import { background, layout, typography } from 'styled-system';
// import { } from 'styled-system';

export const MainBanner = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
`;

export const Title = styled.span`
    display: flex;
    gap: 8px;
    background-color: #F2CB04;
    justify-content: center;
    align-items: center;
    width: 392px;
    height: 92px;
    color: black;
    font-size: 36px;
    text-transform: uppercase;
`

export const SpanTitle = styled.span`
    font-weight: bold;
`

export const Contact = styled.div`
    display: flex;
    padding: 35px 80px;
    margin-top: 80px;
`

export const ContactTitle = styled.span`
    color: #fff;
    max-width: 548px;
    font-size: 36px;
    font-family: Inter;
    font-weight: 200;
    padding: 24px 0px;
    margin-top: 30px;

    background-size: contain;
    background-repeat: no-repeat;

    ${layout}
    ${background}
`

export const Form = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    color: #fff;
    padding: 10px 0px;
`

export const InputTitle = styled.span`
    display: flex;
    flex-direction: column;
    font-size: 18px;
    font-weight: 400;
    color: #fff;
    padding: 10px 0px;
`

export const FormInput = styled.input`
    width: 508px;
    height: 48px;
    background: rgba(255, 255, 255, 0.3);
    backdrop-filter: blur(50px);
    border-radius: 4px;
    margin-top: 10px;
    border: 1px solid black;
    color: #ffffff7d;
    font-size: 16px;
    font-family: Inter;
`


export const LinkBtn = styled.a`
  display: flex;
  align-items: center;
  justify-content: center;
  align-self: flex-end;
  gap: 14px;
  padding: 10px 20px;
  margin-top: 14px;
  cursor: pointer;
  text-decoration: none;
  max-width: 210px;
  border-radius: 7px;
  border: 2px solid #ff9100;
  color: #fff;
  font-weight: bold;
  font-size: 24px;
  transition: 0.3s;
  background-color: #ff9100;

  ${typography}

  transition: 0.3s;

  :hover {
      background-color: #FF9100;
  }
`;
