import React from "react";

import { Props } from "./types";
import * as Styled from "./style";

import ArrowIcon from '../../assets/iconArrow.svg';
import Gradient from '../../assets/gradientTitle.png'

const Banner = ({}: Props) => {
  return (
    <Styled.MainBanner alignItems={['center', 'center', 'flex-start']}
    textAlign={[ 'center', 'center', 'left' ]}>

      <Styled.Title fontSize={[32, 42, 86]} >
        Funk explode em
        <Styled.SubTitle fontSize={[32, 42, 86]} >
          conteúdos exclusivos?
        </Styled.SubTitle>
      </Styled.Title>



      <Styled.Description fontSize={[20, 24, 32]} >
        Já pensou?! Ter acesso a músicas e videoclipes 
        exclusivos do: MC Luan da BS, MC L Da Vinte,
        MC Kaio e cia.?
      </Styled.Description>

      <Styled.BtnTitle fontSize={[20, 24, 32]} >
        Então, se <Styled.BtnTitleGlow>inscreve</Styled.BtnTitleGlow> aí
      </Styled.BtnTitle>

      <Styled.LinkBtn fontSize={[16, 24, 28]} >
        Saiba mais <ArrowIcon />
      </Styled.LinkBtn>

    </Styled.MainBanner>
  )
};

export default Banner;
