import styled from 'styled-components';
import { background, layout, style, typography } from 'styled-system';

export const MainBanner = styled.div`
    display: flex;
    height: 40vh;
`;

export const Description = styled.div`
    height: 100px;
    width: 100px;
    color: #fff;
    padding: 50px 0px;

    background-size: contain;
    background-repeat: no-repeat;

    ${typography}
    ${layout}
    ${background}
`
