import styled from 'styled-components';
import { background, layout, style, typography } from 'styled-system';

export const MainBanner = styled.div`
    display: flex;
`;

export const Description = styled.div`
    font-family: Inter;
    font-style: normal;
    font-weight: 600;
    font-size: 32px;
    max-width: 746px;
    line-height: 125%;
    color: #fff;
    padding: 20px 0px;

    background-size: contain;
    background-repeat: no-repeat;

    ${typography}
    ${layout}
    ${background}
`
