import styled from "styled-components";
import { layout, typography, flexbox } from "styled-system";

import { StyledProps } from "./types";

export const MainHeader = styled.div<StyledProps>`
  display: flex;
  height: 100px;
  background: #1C0426;
  backdrop-filter: blur(8px);
  align-items: center;
  padding: 0px 50px;
  justify-content: space-between;
  ${layout};
  ${flexbox};

`;

export const Title = styled.span<StyledProps>`
`;

export const NavBar = styled.div`
  display: flex;
  ${layout};
`

export const Btn = styled.a`
  cursor: pointer;
  text-decoration: none;
  background-color: #ff9100;
  border-radius: 7px;
  border: 2px solid #ff9100;
  padding: 10px 20px;
  color: #fff;
  font-weight: bold;
  transition: 0.3s;
  ${layout};

  :hover {
      transform: scale(1.1);
    }
  }
`

export const Link = styled.a`
  cursor: pointer;
  font-size: 20px;
  margin: 0px 15px;
  color: #fff;
  font-weight: bold;


  &:hover {
    border-bottom: 2px solid #D90D92;
  }
`
