import React from "react";

import { Props } from "./types";
import * as Styled from "./style";

import borderGradient from '../../assets/contactBorder.png'
import ArrowIcon from '../../assets/iconArrow.svg';


const Banner = ({}: Props) => {
  return (
    <>
      <Styled.TitleContainer width={[150, 290, 392]}
      height={[90, 60, 92]}>
        <Styled.Title 
        fontSize={[15, 24, 36]}>
        Fique por <Styled.SpanTitle>dentro</Styled.SpanTitle> 
        </Styled.Title>
      </Styled.TitleContainer>
      <Styled.MainBanner>

          <Styled.ContactTitle 
          fontSize={[24, 28, 46]}
          textAlign={['center', 'center', 'left']}>
            Para ficar por dentro das novidades que v√£o acontecer, inscreva-se com seu e-mail:
          </Styled.ContactTitle>

          <Styled.Form alignItems={['center', 'center', 'none']}>

            <Styled.InputTitle>
              Nome:
              <Styled.FormInput width={[508, 548, 548]}/>
            </Styled.InputTitle>

            <Styled.InputTitle>
              Email:
              <Styled.FormInput width={[508, 548, 548]}/>
            </Styled.InputTitle>

            <Styled.LinkBtn fontSize={[20, 24, 26]} >
              Enviar <ArrowIcon />
            </Styled.LinkBtn>

          </Styled.Form>
      </Styled.MainBanner>
    </>
  )  
};

export default Banner;
