import React from "react";

import { Props } from "./types";
import * as Styled from "./style";

import borderGradient from '../../assets/contactBorder.png'
import ArrowIcon from '../../assets/iconArrow.svg';

const Banner = ({}: Props) => {
  return (
    <Styled.MainBanner>
      <Styled.Title>
        Fique por <Styled.SpanTitle>dentro</Styled.SpanTitle> 
      </Styled.Title>

      <Styled.Contact flexDirection={['column', 'column', 'row']}
      alignItems={['center', 'none', 'none']}
      textAlign={[ 'none', 'none', 'none' ]}>

        <Styled.ContactTitle 
        backgroundImage={[`url(${borderGradient})`, `url(${borderGradient})`, `url(${borderGradient})`]}
        fontSize={[10, 24, 36]}>
          Para ficar por dentro das novidades que v√£o acontecer, inscreva-se com seu e-mail:
        </Styled.ContactTitle>

        <Styled.Form >

          <Styled.InputTitle>
            Nome:
            <Styled.FormInput/>
          </Styled.InputTitle>

          <Styled.InputTitle>
            Email:
            <Styled.FormInput/>
          </Styled.InputTitle>

          <Styled.LinkBtn fontSize={[16, 24, 28]} 
          alignSelf={['center', 'center', 'flex-end']} >
            Enviar <ArrowIcon />
          </Styled.LinkBtn>

        </Styled.Form>
      </Styled.Contact>
    </Styled.MainBanner>
  )  
};

export default Banner;
