import React from "react";

import { Props } from "./types";
import * as Styled from "./style";

import borderGradient from '../../assets/contactBorder.png'
import ArrowIcon from '../../assets/iconArrow.svg';

const Banner = ({}: Props) => {
  return (
    <Styled.MainBanner>
      <Styled.Title>
        Fique por <Styled.SpanTitle>dentro</Styled.SpanTitle> 
      </Styled.Title>

      <Styled.Contact flexDirection={['column', 'column', 'row']}
      alignItems={['none', 'none', 'none']}
      textAlign={[ 'none', 'none', 'none' ]}>

        <Styled.ContactTitle 
        backgroundImage={[`url(${borderGradient})`, `url(${borderGradient})`, `url(${borderGradient})`]}
        fontSize={[24, 28, 36]}
        textAlign={['center', 'center', 'left']}>
          Para ficar por dentro das novidades que v√£o acontecer, inscreva-se com seu e-mail:
        </Styled.ContactTitle>

        <Styled.Form alignItems={['center', 'center', 'none']}>

          <Styled.InputTitle>
            Nome:
            <Styled.FormInput width={[508, 508, 548]}/>
          </Styled.InputTitle>

          <Styled.InputTitle>
            Email:
            <Styled.FormInput width={[508, 508, 548]}/>
          </Styled.InputTitle>

          <Styled.LinkBtn fontSize={[20, 24, 26]} >
            Enviar <ArrowIcon />
          </Styled.LinkBtn>

        </Styled.Form>
      </Styled.Contact>
    </Styled.MainBanner>
  )  
};

export default Banner;
